sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here
    var color = 'color: #454D5B ;';
    var font_size = 'font-size:12px';
    var copy_right = gs.getProperty("x_1027072_test.u_copy_right");
    if (!copy_right) {
        copy_right = "&copy;2023 Test All rights reserved.";
    }
    template.print('<p style="text-align: center; ' + color + '; ' + font_size + ';">' + copy_right + '</p>');

})(current, template, email, email_action, event);
